import React from "react";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import shortString from "../../../../functions/string";
import ListItemButton from "@mui/material/ListItemButton";
import Typography from "@mui/material/Typography";
const styles = {
  root: {
    background: "#424242",
    width: "95%",
    padding: 0,
    margin: "auto",
    borderRadius:"8px"
  },
  text: {
    color: "#D9E2EC",
  },
  item: {
    padding: 3,
  },
  avatar: {marginLeft:3, width: 30, height: 30 },
};
const ChannelItem = ({ channel, message }) => {
  return (
    <ListItemButton style={styles.root}>
      <ListItem style={styles.item}>
        <ListItemAvatar>
          <Avatar style={styles.avatar}>#</Avatar>
        </ListItemAvatar>
        <ListItemText style={styles.text}>
          <Typography variant="body1">{channel.name}</Typography>
        </ListItemText>
      </ListItem>
    </ListItemButton>
  );
};

export default ChannelItem;
