import React from "react";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import { List, Grid, ListItem, Typography, Avatar } from "@mui/material";
import MessageForm from "./MessageForm/MessageForm";

const styles = {
  root: {
    width: "100%",
    height: "100%",
    backgroundColor: "blue",
  },
  middleContainer: {
    overflowY: "auto",
  },
};
const Channel = ({ messages, channel, addMessage }) => {
  return (
    <Grid sx={{ height: "100vh" }} container direction="column">
      <Grid item xs={1} md={1} lg={1} xl={1}>
        <Typography variant="h5">{channel.name}</Typography>
      </Grid>
      <Grid
        id="middleContainer"
        item
        xs={9}
        md={9}
        lg={9}
        xl={9}
        style={styles.middleContainer}
      >
        <List sx={{ width: "100%", bgcolor: "background.paper" }}>
          {messages?.map((message) => (
            <ListItem>
              <ListItemAvatar>
                <Avatar></Avatar>
              </ListItemAvatar>
              <ListItemText
                primary={<Typography>{message.author}</Typography>}
                secondary={
                  <div style ={{display:'flex'}}>
                    <Typography
                      variant="caption"
                      dangerouslySetInnerHTML={{ __html: message?.content }}
                      style={{
                        padding: 0,
                        color: "black",
                      }}
                    ></Typography>
                    <Typography>{(new Date(message.creation)).getHours().toString()}</Typography>
                  </div>
                }
              />
            </ListItem>
          ))}
        </List>
      </Grid>
      <Grid item xs={2} md={2} lg={2} xl={2}>
        <MessageForm addMessage={addMessage} />
      </Grid>
    </Grid>
  );
};

export default Channel;
