import React from "react";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import { List, Grid, ListItem, Typography, Avatar } from "@mui/material";
import MessageForm from "./MessageForm/MessageForm";

const styles = {
  root: {
    width: "100%",
    height: "100%",
    backgroundColor: "blue",
  },
  middleContainer: {
    overflowY: "auto",
  },
};
const Channel = ({ messages, channel, addMessage }) => {
  return (
    <Grid sx={{ height: "100%" }} container direction="column">
      <Grid item xs={1} md={1} lg={1} xl={1}>
        <Typography variant="h5">{channel.name}</Typography>
      </Grid>
      <Grid item xs={9} md={9} lg={9} xl={9} style={styles.middleContainer}>
        <List sx={{ width: "100%", bgcolor: "background.paper" }}>
          {messages?.map((message) => (
            <ListItem>
              <ListItemAvatar>
                <Avatar></Avatar>
              </ListItemAvatar>
              <ListItemText
                primary={
                  <div
                    dangerouslySetInnerHTML={{ __html: message?.content }}
                    style={{
                      fontSize: "15px",
                      padding: 0,
                      color: "black",
                    }}
                  ></div>
                }
                secondary={
                  <Typography variant="body">
                    {new Date(message.creation).getHours().toString()}
                  </Typography>
                }
              />
            </ListItem>
          ))}
        </List>
      </Grid>
      <Grid item xs={2} md={2} lg={2} xl={2}>
        <MessageForm addMessage={addMessage} />
      </Grid>
    </Grid>
  );
};

export default Channel;
