import React from "react";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import { List, Grid, ListItem, Typography, Avatar } from "@mui/material";
import MessageForm from "./MessageForm/MessageForm";

const styles = {
  root: {
    width: "100%",
    height: "100%",
  },
  middleContainer: {
    overflowY: "auto",
  },
  header: {
    display: "flex",
    alignItems: "center",
    padding: "5px",
    justifyContent: "space-around",
  },
};

const getCurrentTime = () => {
  var today = new Date();
  var time =
    today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
  return time;
};

const Channel = ({ messages, channel, addMessage }) => {
  return (
    <Grid sx={{ height: "100vh" }} container direction="column">
      <Grid style={styles.header} item xs={1} md={1} lg={1} xl={1}>
        <Typography variant="h5">{channel.name}</Typography>
      </Grid>
      <Grid
        id="middleContainer"
        item
        xs={10}
        md={10}
        lg={10}
        xl={10}
        style={styles.middleContainer}
      >
        <List sx={{ width: "100%", bgcolor: "background.paper" }}>
          {messages?.map((message) => (
            <ListItem>
              <ListItemAvatar>
                <Avatar></Avatar>
              </ListItemAvatar>
              <ListItemText
                primary={<Typography>{message.author}</Typography>}
                secondary={
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                    }}
                  >
                    <Typography
                      variant="caption"
                      dangerouslySetInnerHTML={{ __html: message?.content }}
                      style={{
                        padding: 0,
                        color: "black",
                      }}
                    ></Typography>
                    <Typography variant="caption">
                      -{getCurrentTime()}
                    </Typography>
                  </div>
                }
              />
            </ListItem>
          ))}
        </List>
      </Grid>
      <Grid item xs={1} md={1} lg={1} xl={1}>
        <MessageForm addMessage={addMessage} />
      </Grid>
    </Grid>
  );
};

export default Channel;
