import React, { useState } from "react";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import { List, Grid, ListItem, Typography, Avatar, Paper } from "@mui/material";
import MessageForm from "./MessageForm/MessageForm";
const styles = {
  grid: { height: "100vh", background: "#212121" },
  middleContainer: {
    overflowY: "auto",
  },
  header: {
    display: "flex",
    alignItems: "center",
    padding: "5px",
    justifyContent: "space-around",
  },
  message: {
    background: "#424242",
    padding: "5px",
  },
  item: {
    background: "transparent",
  },
};

const Channel = ({ messages, channel, addMessage }) => {
  const [time, setTime] = useState();
  return (
    <Grid sx={styles.grid} container direction="column">
      <Grid style={styles.header} item xs={1} md={1} lg={1} xl={1}>
        <Typography variant="h5">{channel.name}</Typography>
      </Grid>
      <Grid
        id="middleContainer"
        item
        xs={10}
        md={10}
        lg={10}
        xl={10}
        style={styles.middleContainer}
      >
        <List sx={{ width: "100%", bgcolor: "transparent" }}>
          {messages?.map((message) => (
            <ListItem sx={styles.item}>
              <ListItemAvatar>
                <Avatar></Avatar>
              </ListItemAvatar>
              <Paper elevation={4} sx={styles.message}>
                <ListItemText
                  primary={<Typography>{message.author}</Typography>}
                  secondary={
                    <div
                      style={{
                        display: "flex",
                        alignItems: "center",
                      }}
                    >
                      <Typography
                        variant="caption"
                        dangerouslySetInnerHTML={{ __html: message?.content }}
                        style={{
                          padding: 0,
                          color: "light-gray",
                        }}
                      ></Typography>
                      <Typography variant="caption">-{time}</Typography>
                    </div>
                  }
                />
              </Paper>
            </ListItem>
          ))}
        </List>
      </Grid>
      <Grid item xs={1} md={1} lg={1} xl={1}>
        <MessageForm setTime={setTime} addMessage={addMessage} />
      </Grid>
    </Grid>
  );
};

export default Channel;
